import React, { useState, useEffect } from 'react'
import { inject, observer } from 'mobx-react'
import { withStyles, makeStyles } from '@material-ui/core/styles';
import { Table, Paper, TableBody, TableCell, TableContainer, TableHead, TableRow } from '@material-ui/core'
import ChipsArray from '../search/chipSearch';
import { Multiselect } from "multiselect-react-dropdown";


const StyledTableCell = withStyles((theme) => ({
  head: {
    backgroundColor: '#cfd8dc',
    color: '#263238',
    fontSize: 18,
  },
  body: {
    fontSize: 14,
  },
}))(TableCell);

const StyledTableRow = withStyles((theme) => ({
  root: {
    '&:nth-of-type(odd)': {
      backgroundColor: theme.palette.action.hover,
    },
  },
}))(TableRow);

function createData(surname, name, country, firstContact, email, sold, owner,asset_type) {
  return { surname, name, country, firstContact, email, sold, owner,asset_type };
}

const useStyles = makeStyles({
  table: {
    minWidth: 700,
  },
});

/**
 * This client function is exposed and routed from "/" in App.js
 * Functions
 * 1.Injects CRMStores where we have the list of "actions","observers","actions"
 * 2.It calls React Hooks
 *  a.useState which will set input as the passed state and setInput = function to update the state
 * 
 */

const Clients = inject("CRMStores")(observer((props) => {

  //Sets the relevant clients in the state and then updates the state by calling setRelevantClients

  const [relevantClients, setRelevantClients] = useState([...props.CRMStores?.clients])
  const [searchAssets, setSearchAssets] = useState([...props.CRMStores?.searchAssets])
  const [searchOwners, setSearchOwners] = useState([...props.CRMStores?.searchOwners])
  const [owners, setOwners] = useState([...props.CRMStores?.owners])
  const [asset_type, setAssetType] = useState([...props.CRMStores?.assetType])

  const [input, setInput] = useState('')
  const [selected, setSelected] = useState([]);

  /**
   * Use effect is called when render is completed and we want to update anything after that
   */


  useEffect(() => {
    const fetchData = async () => {
      await props.CRMStores.getClients()
      setRelevantClients(props.CRMStores.clients)
      console.log(relevantClients)
    }
    fetchData()
  }, [])

  /**
   * We can pass in a function in order to clear things when effect has completed
   */
  useEffect(() => {
    let tempList = props.CRMStores.clients.filter(c => {
      return c.last.toLowerCase().includes(input.toLowerCase())
    })
    setRelevantClients(tempList)
  }, [input.length])

  /**
   * Filter client list according to selection
   */
   useEffect(() => {
     debugger;
    let tempList = props.CRMStores.clients.filter(c => {
      return searchAssets.includes(c.asset_type)
    })
    setRelevantClients(tempList)
  }, [searchAssets.length])

  useEffect(() => {
    let tempList = props.CRMStores.clients.filter(c => {
      return c.last.toLowerCase().includes(input.toLowerCase())
    })
    setRelevantClients(tempList)
  }, [selected.length])

  const handleChange = (event) => {
    debugger;
    setInput(event.target.value)
  }

  const rows = relevantClients && relevantClients.map(c => {
    return (createData(
      c.last, c.first, c.country_name, c.date, c.email, c.sold, c.owner,c.asset_type))
  })

  function onSelect(selectedList, selectedItem) {
    console.log("Selectd")
  }

  const classes = useStyles();
  return (
    <div><Multiselect
    options={asset_type}
    isObject={false}
    value={selected}
    onSelect={onSelect} // Function will trigger on select event
    onRemove={this.onRemove}
    // onChange={setSelected}
      />
      <TableContainer component={Paper}>
        <Table className={classes.table} aria-label="customized table">
          <TableHead>
            <TableRow>
              <StyledTableCell align="center">Client Name</StyledTableCell>
              <StyledTableCell align="center">Client Email</StyledTableCell>
              <StyledTableCell align="center">Country</StyledTableCell>
              <StyledTableCell align="center">Asset Type</StyledTableCell>
              <StyledTableCell align="center">Mediator</StyledTableCell>
              <StyledTableCell align="center">Owner Company</StyledTableCell>
              <StyledTableCell align="center">Purchase Date</StyledTableCell>
              <StyledTableCell align="center">Sold Date</StyledTableCell>
              {/* <StyledTableCell align="center">Email</StyledTableCell>
              <StyledTableCell align="center">Email</StyledTableCell> */}

              {/* <StyledTableCell align="center">Sold</StyledTableCell>
              <StyledTableCell align="center">Owner</StyledTableCell> */}
            </TableRow>
          </TableHead>
          <TableBody>
            {rows.length && rows.map((row) => (
              <StyledTableRow >
                <StyledTableCell align="center">{row.surname} {row.name}</StyledTableCell>
                <StyledTableCell align="center">{row.email}</StyledTableCell>
                <StyledTableCell align="center">{row.country}</StyledTableCell>
                <StyledTableCell align="center">{row.asset_type}</StyledTableCell>
                <StyledTableCell align="center">GRIP INVEST</StyledTableCell>
                <StyledTableCell align="center">{row.owner}</StyledTableCell>
                <StyledTableCell align="center">{row.firstContact}</StyledTableCell>
                <StyledTableCell align="center">-</StyledTableCell>
              </StyledTableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
      </div>
  )
}))

export default Clients
